		// TODO Auto-generated method stub
		// 변수의 타입
		// 정수타입
		byte byteVar = 1;
		short shortVar = 2;
		int intVar = 3;
		long longVar = 30000000000000L;

		// 정수이면서 문자
		char charVar = 44032; // '가'와 같음

		// 소수 타입
		float floatVar = 3.14f;
		double douleVar = 3.14;

		// boolean
		boolean boolVar = false;

		// string
		String strToday = "오늘은 1월 22일";
		System.out.println(strToday);

